// ━━━ SpokedPy JavaScript Engine ━━━
// Node.js hardware monitor — real system data

const os = require('os');

console.log('='.repeat(44));
console.log('  SpokedPy — Node.js Hardware Monitor');
console.log('='.repeat(44));

// System info
console.log('  Hostname  : ' + os.hostname());
console.log('  Platform  : ' + os.platform() + ' ' + os.arch());
console.log('  OS        : ' + os.type() + ' ' + os.release());
console.log('  Node      : ' + process.version);
console.log('  CPUs      : ' + os.cpus().length);
console.log('  CPU Model : ' + os.cpus()[0].model);

// Memory
const totalMem = os.totalmem();
const freeMem  = os.freemem();
const usedMem  = totalMem - freeMem;
const pct      = ((usedMem / totalMem) * 100).toFixed(1);
console.log('-'.repeat(44));
console.log('  RAM total : ' + (totalMem / 1024 / 1024 / 1024).toFixed(1) + ' GB');
console.log('  RAM used  : ' + (usedMem / 1024 / 1024 / 1024).toFixed(1) + ' GB (' + pct + '%)');
console.log('  RAM free  : ' + (freeMem / 1024 / 1024 / 1024).toFixed(1) + ' GB');

// Uptime
const upSec = os.uptime();
const upH = Math.floor(upSec / 3600);
const upM = Math.floor((upSec % 3600) / 60);
console.log('  Uptime    : ' + upH + 'h ' + upM + 'm');

// CPU usage snapshot
const cpus = os.cpus();
let totalIdle = 0, totalTick = 0;
for (const cpu of cpus) {
    for (const type in cpu.times) totalTick += cpu.times[type];
    totalIdle += cpu.times.idle;
}
const cpuUsage = ((1 - totalIdle / totalTick) * 100).toFixed(1);
console.log('  CPU avg   : ' + cpuUsage + '% (since boot)');

// Node.js process memory
const mem = process.memoryUsage();
console.log('-'.repeat(44));
console.log('  Node heap : ' + (mem.heapUsed / 1024 / 1024).toFixed(1) + ' MB / ' + (mem.heapTotal / 1024 / 1024).toFixed(1) + ' MB');
console.log('  Node RSS  : ' + (mem.rss / 1024 / 1024).toFixed(1) + ' MB');

// Network interfaces
const nets = os.networkInterfaces();
const ifaceNames = Object.keys(nets).filter(n => !n.includes('Loopback') && !n.startsWith('lo'));
console.log('  Net ifaces: ' + ifaceNames.length + ' active');
for (const name of ifaceNames.slice(0, 3)) {
    const ipv4 = nets[name].find(n => n.family === 'IPv4');
    if (ipv4) console.log('    ' + name.substring(0, 18) + ': ' + ipv4.address);
}

console.log('-'.repeat(44));
console.log('  Measured inside SpokedPy runtime');
console.log('='.repeat(44));

// ━━━ SpokedPy TypeScript Engine ━━━
// Typed performance analyzer with interfaces

import * as os from 'os';

interface SystemSnapshot {
    hostname: string;
    platform: string;
    cpuCount: number;
    cpuModel: string;
    totalMemGB: number;
    freeMemGB: number;
    usedPct: number;
    uptimeHours: number;
}

interface BenchmarkResult {
    name: string;
    iterations: number;
    elapsedMs: number;
    opsPerSec: number;
}

function captureSystem(): SystemSnapshot {
    const total = os.totalmem();
    const free  = os.freemem();
    return {
        hostname:    os.hostname(),
        platform:    `${os.platform()} ${os.arch()}`,
        cpuCount:    os.cpus().length,
        cpuModel:    os.cpus()[0]?.model || 'unknown',
        totalMemGB:  +(total / 1073741824).toFixed(1),
        freeMemGB:   +(free / 1073741824).toFixed(1),
        usedPct:     +((1 - free / total) * 100).toFixed(1),
        uptimeHours: +(os.uptime() / 3600).toFixed(1),
    };
}

function benchmark(name: string, iterations: number, fn: () => void): BenchmarkResult {
    const t0 = Date.now();
    for (let i = 0; i < iterations; i++) fn();
    const elapsed = Date.now() - t0;
    return {
        name,
        iterations,
        elapsedMs: elapsed,
        opsPerSec: Math.round(iterations / (elapsed / 1000)),
    };
}

// Capture
const snap = captureSystem();

console.log('='.repeat(47));
console.log('  SpokedPy — TypeScript Performance Analyzer');
console.log('='.repeat(47));
console.log(`  Host     : ${snap.hostname}`);
console.log(`  Platform : ${snap.platform}`);
console.log(`  CPUs     : ${snap.cpuCount} x ${snap.cpuModel.substring(0, 30)}`);
console.log(`  Memory   : ${snap.usedPct}% used (${snap.freeMemGB}/${snap.totalMemGB} GB free)`);
console.log(`  Uptime   : ${snap.uptimeHours} hours`);
console.log('-'.repeat(47));

// Benchmarks
const results: BenchmarkResult[] = [];

results.push(benchmark('Array fill', 50000, () => {
    const a = new Array(100).fill(0).map((_, i) => i * i);
}));

results.push(benchmark('String concat', 20000, () => {
    let s = '';
    for (let i = 0; i < 50; i++) s += String.fromCharCode(65 + (i % 26));
}));

results.push(benchmark('Object create', 50000, () => {
    const o = { x: Math.random(), y: Math.random(), z: Math.random() };
}));

console.log('  Benchmarks (inside SpokedPy):');
for (const r of results) {
    const bar = '#'.repeat(Math.min(20, Math.round(r.opsPerSec / 100000)));
    console.log(`    ${r.name.padEnd(14)} ${String(r.elapsedMs).padStart(5)}ms  ${r.opsPerSec.toLocaleString().padStart(12)} ops/s  ${bar}`);
}

console.log('-'.repeat(47));
console.log('  All metrics captured from within SpokedPy');
console.log('='.repeat(47));

// ━━━ SpokedPy Rust Engine ━━━
// High-performance CPU benchmark with system info

use std::time::Instant;
use std::env;

fn is_prime(n: u64) -> bool {
    if n < 2 { return false; }
    if n < 4 { return true; }
    if n % 2 == 0 || n % 3 == 0 { return false; }
    let mut i = 5u64;
    while i * i <= n {
        if n % i == 0 || n % (i + 2) == 0 { return false; }
        i += 6;
    }
    true
}

fn main() {
    println!("================================================");
    println!("  SpokedPy — Rust CPU Benchmark");
    println!("================================================");

    // Environment info
    let cwd = env::current_dir().unwrap_or_default();
    println!("  Working dir : {}", cwd.display());
    println!("  Rust target : {}", env::consts::ARCH);
    println!("  OS          : {} {}", env::consts::OS, env::consts::FAMILY);

    // Prime sieve benchmark
    let limit = 100_000u64;
    let t0 = Instant::now();
    let mut count = 0u64;
    let mut last = 0u64;
    for n in 2..=limit {
        if is_prime(n) {
            count += 1;
            last = n;
        }
    }
    let elapsed = t0.elapsed();

    println!("------------------------------------------------");
    println!("  Prime sieve to {}:", limit);
    println!("    Primes found : {}", count);
    println!("    Largest      : {}", last);
    println!("    Time         : {:.3}ms", elapsed.as_secs_f64() * 1000.0);
    println!("    Speed        : {:.0} checks/sec",
             limit as f64 / elapsed.as_secs_f64());

    // Fibonacci benchmark
    let fib_n = 40u64;
    let t1 = Instant::now();
    let mut a: u64 = 0;
    let mut b: u64 = 1;
    for _ in 0..fib_n {
        let tmp = a + b;
        a = b;
        b = tmp;
    }
    let fib_time = t1.elapsed();
    println!("------------------------------------------------");
    println!("  Fibonacci({}): {}", fib_n, b);
    println!("    Time: {:.3}us", fib_time.as_nanos() as f64 / 1000.0);

    // Memory allocation benchmark
    let t2 = Instant::now();
    let v: Vec<u64> = (0..500_000).map(|i| i * i).collect();
    let alloc_time = t2.elapsed();
    println!("------------------------------------------------");
    println!("  Vec alloc 500K u64:");
    println!("    Time  : {:.3}ms", alloc_time.as_secs_f64() * 1000.0);
    println!("    Sum   : {}", v.iter().take(100).sum::<u64>());
    println!("    Size  : {} KB", v.len() * 8 / 1024);

    println!("------------------------------------------------");
    println!("  Compiled & measured inside SpokedPy");
    println!("================================================");
}

// ━━━ SpokedPy Java Engine ━━━
// JVM resource monitor with runtime metrics

import java.lang.management.*;
import java.util.*;

public class Main {
    public static void main(String[] args) {
        Runtime rt = Runtime.getRuntime();
        OperatingSystemMXBean osMX = ManagementFactory.getOperatingSystemMXBean();
        RuntimeMXBean rtMX = ManagementFactory.getRuntimeMXBean();

        System.out.println("================================================");
        System.out.println("  SpokedPy — Java JVM Resource Monitor");
        System.out.println("================================================");

        // System info
        System.out.printf("  OS         : %s %s (%s)%n",
            osMX.getName(), osMX.getVersion(), osMX.getArch());
        System.out.printf("  Java       : %s (%s)%n",
            System.getProperty("java.version"),
            System.getProperty("java.vm.name"));
        System.out.printf("  Processors : %d%n", rt.availableProcessors());

        // JVM Memory
        long maxMem   = rt.maxMemory();
        long totalMem = rt.totalMemory();
        long freeMem  = rt.freeMemory();
        long usedMem  = totalMem - freeMem;

        System.out.println("------------------------------------------------");
        System.out.printf("  Heap max   : %,d MB%n", maxMem / (1024 * 1024));
        System.out.printf("  Heap total : %,d MB%n", totalMem / (1024 * 1024));
        System.out.printf("  Heap used  : %,d MB%n", usedMem / (1024 * 1024));
        System.out.printf("  Heap free  : %,d MB%n", freeMem / (1024 * 1024));

        // CPU benchmark
        System.out.println("------------------------------------------------");
        long t0 = System.nanoTime();
        long sum = 0;
        for (int i = 0; i < 2_000_000; i++) {
            sum += (long) i * i;
        }
        double elapsedMs = (System.nanoTime() - t0) / 1_000_000.0;
        System.out.printf("  Benchmark  : 2M iterations in %.1fms%n", elapsedMs);
        System.out.printf("  Throughput : %,.0f ops/sec%n", 2_000_000.0 / (elapsedMs / 1000));
        System.out.printf("  Checksum   : %d%n", sum % 999_999_937);

        // GC info
        List<GarbageCollectorMXBean> gcBeans = ManagementFactory.getGarbageCollectorMXBeans();
        System.out.println("------------------------------------------------");
        for (GarbageCollectorMXBean gc : gcBeans) {
            String gcName = gc.getName();
            if (gcName.length() > 8) gcName = gcName.substring(0, 8);
            System.out.printf("  GC %-8s: %d collections, %dms%n",
                gcName, gc.getCollectionCount(), gc.getCollectionTime());
        }

        // Thread count
        int threads = ManagementFactory.getThreadMXBean().getThreadCount();
        long jvmUptime = rtMX.getUptime();
        System.out.println("------------------------------------------------");
        System.out.printf("  Threads    : %d active%n", threads);
        System.out.printf("  JVM uptime : %dms%n", jvmUptime);
        System.out.println("  Measured inside SpokedPy runtime");
        System.out.println("================================================");
    }
}

// ━━━ SpokedPy Go Engine ━━━
// Goroutine-powered system probe with runtime metrics

package main

import (
	"fmt"
	"os"
	"runtime"
	"sync"
	"time"
)

func cpuBench(iterations int, wg *sync.WaitGroup, results chan<- float64) {
	defer wg.Done()
	t0 := time.Now()
	sum := int64(0)
	for i := 0; i < iterations; i++ {
		sum += int64(i) * int64(i)
	}
	results <- time.Since(t0).Seconds()
}

func main() {
	fmt.Println("================================================")
	fmt.Println("  SpokedPy — Go Goroutine System Probe")
	fmt.Println("================================================")

	// System info
	hostname, _ := os.Hostname()
	fmt.Printf("  Hostname    : %s\n", hostname)
	fmt.Printf("  OS/Arch     : %s/%s\n", runtime.GOOS, runtime.GOARCH)
	fmt.Printf("  Go version  : %s\n", runtime.Version())
	fmt.Printf("  CPUs        : %d\n", runtime.NumCPU())
	fmt.Printf("  GOMAXPROCS  : %d\n", runtime.GOMAXPROCS(0))

	// Memory stats
	var m runtime.MemStats
	runtime.ReadMemStats(&m)
	fmt.Println("------------------------------------------------")
	fmt.Printf("  Heap alloc  : %d KB\n", m.HeapAlloc/1024)
	fmt.Printf("  Heap sys    : %d KB\n", m.HeapSys/1024)
	fmt.Printf("  Stack sys   : %d KB\n", m.StackSys/1024)
	fmt.Printf("  GC cycles   : %d\n", m.NumGC)
	fmt.Printf("  Goroutines  : %d\n", runtime.NumGoroutine())

	// Parallel CPU benchmark with goroutines
	numWorkers := runtime.NumCPU()
	if numWorkers > 8 {
		numWorkers = 8
	}
	iterPerWorker := 1_000_000
	results := make(chan float64, numWorkers)
	var wg sync.WaitGroup

	fmt.Println("------------------------------------------------")
	fmt.Printf("  Benchmark: %d goroutines x %dK iterations\n", numWorkers, iterPerWorker/1000)

	t0 := time.Now()
	for i := 0; i < numWorkers; i++ {
		wg.Add(1)
		go cpuBench(iterPerWorker, &wg, results)
	}
	go func() { wg.Wait(); close(results) }()

	var maxWorkerTime float64
	for r := range results {
		if r > maxWorkerTime {
			maxWorkerTime = r
		}
	}
	wallTime := time.Since(t0)

	totalOps := numWorkers * iterPerWorker
	fmt.Printf("  Wall time   : %v\n", wallTime)
	fmt.Printf("  Total ops   : %d\n", totalOps)
	fmt.Printf("  Throughput  : %.0f ops/sec\n", float64(totalOps)/wallTime.Seconds())

	// Post-benchmark memory
	runtime.ReadMemStats(&m)
	fmt.Println("------------------------------------------------")
	fmt.Printf("  Post-bench heap: %d KB (+GC: %d)\n", m.HeapAlloc/1024, m.NumGC)
	fmt.Println("  All goroutines measured inside SpokedPy")
	fmt.Println("================================================")
}
